/**
 * Module dependencies.
 */
var express = require('express');
var routes  = require('app/libs/routes.manager');
var socket  = require('app/libs/socket.manager');
var mapping = require('app/resources/mapping');
var config  = require('app/resources/config');
var app     = module.exports = express.createServer();

app.configure(function() {
  app.set('views', __dirname + '/views');
  app.set('view engine', 'jade');
  app.use(express.bodyParser());
  app.use(express.cookieParser());
  app.use(express.methodOverride());
  app.use(app.router);
  app.use(express.static(__dirname + '/public'));
  app.dynamicHelpers({
    app : function(req, res) { return config.app; }
  });
});

routes.listen(app, mapping.routes);
socket.listen(app, mapping.socket);
console.log("Express server listening on port %d in %s mode", app.address().port, app.settings.env);
